mapper.select:
SELECT 

FROM tbl_product p
WHERE p.category_code = #{categoryCode}
AND p.product_orderable = 'Y'
ORDER BY p.product_code DESC
LIMIT #{pagenation.size} OFFSET #{pagenation.offset}

SELECT COUNT(*)
FROM tbl_product
WHERE category_code = #{categoryCode}

mapper.update:
        UPDATE
			tbl_product
        SET
			product_stock = #{productStock}
        WHERE
			product_code = #{productCode}

UPDATE tbl_product SET product_name = #{productName}, product_price = #{productPrice}, product_description = #{productDescription}, category_code = #{categoryCode}, product_stock = #{productStock}
, product_image_url = #{productImageUrl}
WHERE product_code = #{productCode}
